import React from 'react'
import Pollslist from './pollslist/Pollslist'
import Pollsdetails from './pollsdetails/Pollsdetails'
import '../../index.css'
const Content = ({polls, setPolls}) => {

  const [newPoll, setNewPoll] = useState({
    id: 0,
    question : '',
    image : '',
    results : [0,0,0],
    options : ['','','']
  });


  const addPoll = (poll) => {

    const id = polls.length ? polls[polls.length - 1].id + 1 : 1;
    const myNewPoll = {id, question: poll.question, image: poll.image, results: poll.results, options: poll.options};

    const listPolls = [...polls, myNewPoll];
    setPolls(listPolls);
    localStorage.setItem('pollsList',JSON.stringify(listPolls));

  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!newPoll) return;
    addPoll(newPoll);
    setNewPoll(newPoll => ({
                      ...newPoll,
                      id: 0,
                    question : '',
                    image : '',
                    results : [0,0,0],
                    options : ['','','']  
                    }));
  }

  return (
    <div className='d-flex mt-5'>
      {polls.length ? console.log("3amr"): console.log("walo")}
        <Pollslist 
          polls = {polls}
          setPolls = {setPolls}
        />
        <Pollsdetails />
    </div>
  )
}

export default Content
