import React from 'react'
import Pollslist from './pollslist/Pollslist'
import Pollsdetails from './pollsdetails/Pollsdetails'
import '../../index.css'
const Content = ({polls, setPolls, handleSelectPoll, pollSelected, handleVote,  chartCon, setChartCon, isLoading, setIsLoading, fetchErr, setFetchErr}) => {
  
  return (
    <div className='d-flex mt-5'>
      {fetchErr && <p style={{color : 'red'}}>{`Error : ${fetchErr}`}</p>}
        <Pollslist 
          polls = {polls}
          setPolls = {setPolls}
          handleSelectPoll={handleSelectPoll}
          isLoading={isLoading}
          setIsLoading={setIsLoading}
        />
        <Pollsdetails 
          polls = {polls}
          setPolls = {setPolls}
          handleSelectPoll={handleSelectPoll}
          pollSelected={pollSelected}
          handleVote={handleVote}
          chartCon={chartCon}
          setChartCon={setChartCon}
        />
    </div>
  )
}

export default Content
