import './App.css';
import Head from './components/head/Head';
import Content from './components/content/Content';
import './css/bootstrap.min.css'
import { useState } from 'react';

function App() {

  if(localStorage.getItem('pollsList')){
  const [polls, setPolls] = useState(JSON.parse(localStorage.getItem('pollsList')));
  }else{

  }

  const [newPoll, setNewPoll] = useState({
    id: 0,
    question : '',
    image : '',
    results : [0,0,0],
    options : ['','','']
  });


  const addPoll = (poll) => {

    const id = polls.length ? polls[polls.length - 1].id + 1 : 1;
    const myNewPoll = {id, question: poll.question, image: poll.image, results: poll.results, options: poll.options};

    const listPolls = [...polls, myNewPoll];
    setPolls(listPolls);
    localStorage.setItem('pollsList',JSON.stringify(listPolls));

  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!newPoll) return;
    addPoll(newPoll);
    setNewPoll(newPoll => ({
                      ...newPoll,
                      id: 0,
                    question : '',
                    image : '',
                    results : [0,0,0],
                    options : ['','','']  
                    }));
  }



  const [voter, setVoter] = useState([{
    id : "12345",
    voted : false
  }]);


  return (
    <div className="App">
      <Head 
        newPoll={newPoll}
        setNewPoll={setNewPoll}
        handleSubmit={handleSubmit}
      />
      <Content 
        polls={polls}
        setPolls={setPolls}
      />
    </div>
  );
}

export default App;
