import React from 'react'

function Vote({polls, setPolls, pollSelected, handleVote}) {
  return (
    <form className='form-check m-2' onSubmit={handleVote}>  
        <h1>{pollSelected.question}</h1>
        {pollSelected.options.map(option => 
        (
            <div className='form-check m-2' key={pollSelected.options.indexOf(option)}>
                <input className='form-check-input' type="radio" name="imput" id={option} value={pollSelected.options.indexOf(option)}  />
                <label className='form-check-label' htmlFor={option}>
                    {option}
                </label>
            </div>
        ))}
        <div className='d-flex justify-content-start m-2'>
            <button type="submit" className='btn btn-dark '>Vote</button>
        </div>
    </form>
  )
}

export default Vote
