import React from 'react'

function Vote({polls, setPolls, pollSelected}) {
  return (
    <form className='form-check'>  
        <h1>{pollSelected.question}</h1>
        {pollSelected.options.map(option => 
        (
            <div className='form-check'>
            <input className='form-check-input' type="radio" name="imput" id={option} value={pollSelected.options.in}  />
            <label className='form-check-label' htmlFor={option}>
                {option}
            </label>
            </div>

        ))}
    </form>
  )
}

export default Vote
