  const [polls, setPolls] = useState([
    {
      id: 1,
      question : "Terminee ??",
      image : "https://images.pexels.com/photos/3767411/pexels-photo-3767411.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
      results : [0,5,8],
      options : ["Yes", "No", "Almost"]
    },
    {
      id: 2,
      question : "You love blockchain ??",
      image : "https://cloudfront-us-east-1.images.arcpublishing.com/coindesk/G4Q5EWU6JVA5NCNILS2ID4FDAM.jpg",
      results : [21,0,9],
      options : ["Yes", "No", "Maybe"]
    }
  ]);


  const [newPoll, setNewPoll] = useState({
    id: 0,
    question : '',
    image : '',
    results : [0,0,0],
    options : ['', '', '']
  });

  setNewPoll(newPoll => ({
    ...newPoll,

  }))

  const addPoll = (poll) => {

    const id = polls.length ? polls[polls.length - 1].id + 1 : 1;
    const myNewPoll = {id, question: poll.question, image: poll.image, results: poll.results, options: poll.options};

    const listPolls = [...polls, myNewPoll];
    setPolls(listPolls);
    localStorage.setItem('pollsList',JSON.stringify(listPolls));

