import React from 'react'
import Pollslist from './pollslist/Pollslist'
import Pollsdetails from './pollsdetails/Pollsdetails'
import '../../index.css'
const Content = ({polls, setPolls}) => {

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!newPoll) return;
    addPoll(newPoll);
    setNewPoll(newPoll => ({
                      ...newPoll,
                      id: 0,
                    question : '',
                    image : '',
                    results : [0,0,0],
                    options : ['','','']  
                    }));
  }

  return (
    <div className='d-flex mt-5'>
      {polls.length ? console.log("3amr"): console.log("walo")}
        <Pollslist 
          polls = {polls}
          setPolls = {setPolls}
        />
        <Pollsdetails />
    </div>
  )
}

export default Content
