import React from 'react'

function Vote({polls, setPolls, pollSelected}) {
  return (
    <form>  
        <h1>{pollSelected.question}</h1>
        <div className='form-check'>
            <input className='form-check-input' type="radio" name={pollSelected.option[0]} id="flexRadioDefault1" />
            <label className='form-check-label' htmlFor='flexRadioDefault1'>
                {pollSelected.option[0]}
            </label>
        </div>
    </form>
  )
}

export default Vote
